// --- FUNCIÓN 1: Descargar al cliente ---
function generatePDF(cliente, carrito, fecha = new Date(), idPedido = "Nuevo") {
    const doc = crearDocumentoPDF(cliente, carrito, fecha, idPedido);
    doc.save(`Orden_Jeweler_${idPedido}.pdf`);
}

// --- FUNCIÓN 2: Entregar archivo para subir a Strapi---
function obtenerBlobPDF(cliente, carrito, fecha = new Date(), idPedido = "Nuevo") {
    const doc = crearDocumentoPDF(cliente, carrito, fecha, idPedido);
    return doc.output('blob'); // Devuelve el archivo crudo en memoria
}

// --- LÓGICA COMÚN DE DISEÑO (Para no repetir código) ---
function crearDocumentoPDF(cliente, carrito, fecha, idPedido) {
    if (!window.jspdf) return null;
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF({ unit: "mm", format: "a4" });

    // Fondo Oliva
    doc.setFillColor(161, 154, 88);
    doc.rect(0, 0, 210, 297, "F");

    // Título
    doc.setFont("times", "bold");
    doc.setFontSize(30);
    doc.setTextColor(0, 0, 0);
    doc.text("Jeweler’s Store", 105, 25, { align: "center" });

    // Info
    const fechaTexto = new Date(fecha).toLocaleDateString();
    doc.setFontSize(10);
    doc.setFont("times", "normal");
    doc.text(`Fecha: ${fechaTexto} - Pedido #${idPedido}`, 180, 30, { align: "right" });

    // Cliente
    doc.setFontSize(12);
    doc.text("Datos del cliente", 105, 45, { align: "center" });
    doc.text(`Nombre: ${cliente.nombre}`, 105, 52, { align: "center" });
    doc.text(`Teléfono: ${cliente.telefono}`, 105, 59, { align: "center" });
    
    const splitDireccion = doc.splitTextToSize(`Dirección: ${cliente.direccion}`, 180);
    doc.text(splitDireccion, 105, 66, { align: "center" });

    // Productos
    doc.setFont("times", "bold");
    doc.setFontSize(14);
    let y = 82 + (splitDireccion.length * 5); 
    doc.text("Detalle del pedido", 105, y, { align: "center" });

    doc.setFont("times", "normal");
    doc.setFontSize(12);
    y += 10;
    let total = 0;

    carrito.forEach((item, i) => {
        let subtotal = item.precio * item.cantidad;
        total += subtotal;
        doc.text(`${i + 1}. ${item.nombre} - ${item.cantidad} x C$${item.precio} = C$${subtotal.toFixed(2)}`, 105, y, { align: "center" });
        y += 8;
    });

    doc.setFont("times", "bold");
    doc.setFontSize(13);
    doc.text(`Total a Pagar: C$ ${total.toFixed(2)}`, 105, y + 10, { align: "center" });

    return doc;
}

